      confirmPassword: ''
    },
    {
      email: [
        { type: 'required', message: 'Email is required' },
        { type: 'email', message: 'Please enter a valid email address' }
      ],
      phone: [
        { type: 'required', message: 'Phone number is required' },
        { type: 'phone', message: 'Invalid phone number format' }
      ],
